export const getCookiesFromReq = (req, cookieKey) => {
  const cookie = req.headers.cookie.split(';').find(c => c.trim().startsWith(`${cookieKey}=`))
  if (!cookie) { return undefined }
  return cookie.split('=')[1]
}
