    // }

    if (Component.getInitialProps) {
      pageProps = await Component.getInitialProps(ctx)
    }
    const auth = { user, isAuthenticated }
    return { pageProps, auth }
  }

  render () {
    const { Component, pageProps, auth } = this.props
    return (
      <Container>
        <Component {...pageProps} auth={auth} />
      </Container>
    )
  }
}
